import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import { Link } from "react-router-dom";
import {
  FiCalendar,
  FiBookOpen,
  FiHome,
  FiLogOut,
  FiCheckCircle,
  FiXCircle,
  FiPlus,
} from "react-icons/fi";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const API_URL = process.env.REACT_APP_API_URL;

export default function BookingCalendar() {
  const [selectedDate, setSelectedDate] = useState("");
  const [startTime, setStartTime] = useState("");
  const [endTime, setEndTime] = useState("");
  const [room, setRoom] = useState("");
  const [bookedSlots, setBookedSlots] = useState([]);
  const [confirmationMsg, setConfirmationMsg] = useState("");
  const [status, setStatus] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const today = new Date().toISOString().split("T")[0];

  const hours = [
    "8:00am",
    "9:00am",
    "10:00am",
    "11:00am",
    "12:00pm",
    "1:00pm",
    "2:00pm",
    "3:00pm",
    "4:00pm",
  ];

  const rooms = [
    "Meeting Room A",
    "Meeting Room B",
    "Meeting Room C",
    "Meeting Room D",
    "Meeting Room E",
  ];

  // Helper function to convert time (e.g., "9:00am") to minutes from midnight
  const timeToMinutes = (timeStr) => {
    if (!timeStr) return 0;
    const [time, period] = timeStr.replace(/:\d+/, "").split(/(am|pm)/i);
    let hour = parseInt(time, 10);
    if (period.toLowerCase() === "pm" && hour !== 12) hour += 12;
    if (period.toLowerCase() === "am" && hour === 12) hour = 0;
    return hour * 60;
  };

  // Check if a specific time is booked
  const isTimeSlotBooked = (checkTime) => {
    const checkMinutes = timeToMinutes(checkTime);
    return bookedSlots.some((booked) => {
      const bookedStart = timeToMinutes(booked.start);
      const bookedEnd = timeToMinutes(booked.end);
      return checkMinutes >= bookedStart && checkMinutes < bookedEnd;
    });
  };

  // Check if a time range is available
  const isTimeRangeAvailable = (startTime, endTime) => {
    if (!startTime || !endTime) return true;

    const startMinutes = timeToMinutes(startTime);
    const endMinutes = timeToMinutes(endTime);

    if (startMinutes >= endMinutes) return false;

    return !bookedSlots.some((booked) => {
      const bookedStart = timeToMinutes(booked.start);
      const bookedEnd = timeToMinutes(booked.end);

      return (
        (startMinutes >= bookedStart && startMinutes < bookedEnd) ||
        (endMinutes > bookedStart && endMinutes <= bookedEnd) ||
        (startMinutes <= bookedStart && endMinutes >= bookedEnd)
      );
    });
  };

  // Fetch booked slots
  useEffect(() => {
    if (selectedDate && room) {
      setIsLoading(true);
      axios
        .get(`${API_URL}/api/booking/slots?date=${selectedDate}&room=${room}`)
        .then((res) => {
          const formattedBookedSlots = res.data.map((slot) => ({
            start: formatTimeForDisplay(slot.time),
            end: formatTimeForDisplay(slot.end_time),
          }));
          setBookedSlots(formattedBookedSlots);
        })
        .catch(() => setBookedSlots([]))
        .finally(() => setIsLoading(false));
    } else {
      setBookedSlots([]);
    }
  }, [selectedDate, room]);

  // Helper to format 24hr time from backend to am/pm for display
  const formatTimeForDisplay = (time24) => {
    if (!time24) return "";
    const [hourStr, minuteStr] = time24.split(":");
    let hour = parseInt(hourStr, 10);
    const ampm = hour >= 12 ? "pm" : "am";
    hour = hour % 12;
    hour = hour === 0 ? 12 : hour;
    return `${hour}:${minuteStr}${ampm}`;
  };

  // Format time for display in dropdowns
  const formatTime = (timeStr) => {
    if (!timeStr) return "";
    const [time, period] = timeStr.split(/(am|pm)/i);
    return `${time.trim()}${period ? period.toUpperCase() : ""}`;
  };

  const confirmBooking = () => {
    if (!startTime || !endTime || !room || !selectedDate) {
      setStatus("error");
      setConfirmationMsg("Please select date, time range, and meeting room.");
      return;
    }

    const startMinutes = timeToMinutes(startTime);
    const endMinutes = timeToMinutes(endTime);

    if (startMinutes >= endMinutes) {
      setStatus("error");
      setConfirmationMsg("End time must be after start time.");
      return;
    }

    if (!isTimeRangeAvailable(startTime, endTime)) {
      setStatus("error");
      setConfirmationMsg("Selected time range overlaps with an existing booking.");
      return;
    }

    const payload = {
      date: selectedDate,
      startTime,
      endTime,
      room,
      userEmail: user?.email,
      userName: user?.name,
    };

    setIsLoading(true);
    axios
      .post(`${API_URL}/api/booking/create`, payload)
      .then(() => {
        setStatus("success");
        setConfirmationMsg("Booking confirmed! A confirmation email has been sent.");

        // Refresh booked slots
        axios
          .get(`${API_URL}/api/booking/slots?date=${selectedDate}&room=${room}`)
          .then((res) => {
            const formattedBookedSlots = res.data.map((slot) => ({
              start: formatTimeForDisplay(slot.time),
              end: formatTimeForDisplay(slot.end_time),
            }));
            setBookedSlots(formattedBookatedSlots);
          })
          .catch(() => setBookedSlots([]));

        setStartTime("");
        setEndTime("");
      })
      .catch((err) => {
        console.error("❌ Booking error:", err);
        setStatus("error");
        setConfirmationMsg(
          err.response?.data?.message || "Booking failed. Please try again."
        );
      })
      .finally(() => setIsLoading(false));
  };

  const handleLogout = () => {
    logout()
      .then(() => navigate("/login"))
      .catch((err) => console.error("Logout error:", err));
  };

  return (
    <div className="flex min-h-screen bg-gray-50 font-poppins">
      {/* Sidebar */}
      <div className="w-64 bg-gradient-to-b from-green-700 to-green-900 shadow-xl hidden md:flex flex-col p-6 text-white">
        <div className="mb-10 text-center">
          <h2 className="text-3xl font-extrabold mb-1 text-green-100">MeetingHub</h2>
          <p className="text-green-200 text-sm">Your effortless booking solution</p>
        </div>

        <nav className="flex-1 space-y-3">
          <Link
            to="/dashboard-user"
            className="flex items-center p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all duration-200 ease-in-out group"
          >
            <FiHome className="mr-3 text-xl group-hover:scale-110 transition-transform" />
            <span className="text-lg">Dashboard</span>
          </Link>
          <Link
            to="/my-bookings"
            className="flex items-center p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all duration-200 ease-in-out group"
          >
            <FiBookOpen className="mr-3 text-xl group-hover:scale-110 transition-transform" />
            <span className="text-lg">My Bookings</span>
          </Link>
          <Link
            to="/calendar"
            className="flex items-center p-3 text-white bg-green-800 rounded-lg transition-all duration-200 ease-in-out hover:bg-green-900 shadow-md"
          >
            <FiCalendar className="mr-3 text-xl" />
            <span className="text-lg">New Booking</span>
          </Link>
        </nav>

        <div className="mt-auto pt-5 border-t border-green-800">
          {user && (
            <div className="px-4 py-3 bg-green-800 rounded-lg mb-2 shadow-inner">
              <p className="text-sm font-semibold">{user.name}</p>
              <p className="text-xs text-green-200">{user.email}</p>
            </div>
          )}
          <button
            className="flex items-center w-full p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all duration-200 ease-in-out group"
            onClick={handleLogout}
          >
            <FiLogOut className="mr-3 text-xl group-hover:scale-110 transition-transform" />
            <span className="text-lg">Logout</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8">
        <header className="bg-white shadow-lg p-6 mb-8 rounded-lg flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-800 flex items-center">
              <FiCalendar className="inline-block mr-3 text-green-600" />
              Book a Meeting Room
            </h1>
            <p className="text-gray-600 text-lg mt-1">
              Select a date, time, and room for your meeting
            </p>
          </div>
          {/* You could add more header elements here, like a user icon/dropdown */}
        </header>

        {confirmationMsg && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className={`mb-6 p-4 rounded-lg flex items-center ${
              status === "success"
                ? "bg-green-50 text-green-800 border border-green-300"
                : "bg-red-50 text-red-800 border border-red-300"
            }`}
          >
            {status === "success" ? (
              <FiCheckCircle className="mr-3 text-xl" />
            ) : (
              <FiXCircle className="mr-3 text-xl" />
            )}
            <span className="text-lg">{confirmationMsg}</span>
          </motion.div>
        )}

        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.3 }}
          className="bg-white rounded-xl shadow-lg p-8 max-w-2xl mx-auto"
        >
          <div className="space-y-6">
            <div>
              <label htmlFor="selectedDate" className="block text-base font-medium text-gray-700 mb-2">
                Select a Date
              </label>
              <input
                type="date"
                id="selectedDate"
                min={today}
                value={selectedDate}
                className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-3 border text-gray-800"
                onChange={(e) => {
                  setSelectedDate(e.target.value);
                  setStartTime("");
                  setEndTime("");
                  setConfirmationMsg("");
                  setStatus("");
                }}
              />
            </div>

            <div>
              <label htmlFor="room" className="block text-base font-medium text-gray-700 mb-2">
                Meeting Room
              </label>
              <select
                id="room"
                className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-3 border text-gray-800"
                value={room}
                onChange={(e) => {
                  setRoom(e.target.value);
                  setStartTime("");
                  setEndTime("");
                  setConfirmationMsg("");
                  setStatus("");
                }}
              >
                <option value="">-- Select Room --</option>
                {rooms.map((r) => (
                  <option key={r} value={r}>
                    {r}
                  </option>
                ))}
              </select>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label htmlFor="startTime" className="block text-base font-medium text-gray-700 mb-2">
                  Start Time
                </label>
                <select
                  id="startTime"
                  className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-3 border text-gray-800"
                  value={startTime}
                  onChange={(e) => {
                    setStartTime(e.target.value);
                    setEndTime("");
                  }}
                  disabled={!selectedDate || !room || isLoading}
                >
                  <option value="">-- Select --</option>
                  {hours.map((h) => {
                    const isBooked = isTimeSlotBooked(h);
                    return (
                      <option
                        key={h}
                        value={h}
                        disabled={isBooked}
                        className={isBooked ? "text-gray-400 bg-gray-50" : ""}
                      >
                        {formatTime(h)}{isBooked ? " (Booked)" : ""}
                      </option>
                    );
                  })}
                </select>
              </div>

              <div>
                <label htmlFor="endTime" className="block text-base font-medium text-gray-700 mb-2">
                  End Time
                </label>
                <select
                  id="endTime"
                  className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-3 border text-gray-800"
                  value={endTime}
                  onChange={(e) => setEndTime(e.target.value)}
                  disabled={!startTime || isLoading}
                >
                  <option value="">-- Select --</option>
                  {hours.map((h) => {
                    const isAfterStart = startTime && timeToMinutes(h) > timeToMinutes(startTime);
                    const isAvailable = isAfterStart && isTimeRangeAvailable(startTime, h);

                    return (
                      <option
                        key={h}
                        value={h}
                        disabled={!isAvailable}
                        className={!isAvailable ? "text-gray-400 bg-gray-50" : ""}
                      >
                        {formatTime(h)}{!isAfterStart ? " (Before start)" : !isAvailable ? " (Unavailable)" : ""}
                      </option>
                    );
                  })}
                </select>
              </div>
            </div>

            <button
              className={`w-full flex items-center justify-center bg-green-600 hover:bg-green-700 text-white py-3.5 px-4 rounded-lg shadow-lg font-semibold text-lg transition-colors duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 mt-6 ${
                isLoading ? "opacity-70 cursor-not-allowed" : ""
              }`}
              onClick={confirmBooking}
              disabled={isLoading}
            >
              {isLoading ? (
                "Processing..."
              ) : (
                <>
                  <FiPlus className="inline-block mr-2.5 text-xl" />
                  Confirm Booking
                </>
              )}
            </button>
          </div>
        </motion.div>
      </div>
    </div>
  );
}
