import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import { Link } from "react-router-dom";
import { FiCalendar, FiBookOpen, FiHome, FiLogOut, FiUser, FiClock, FiCheckCircle, FiX, FiEdit2, FiChevronRight, FiPlus } from "react-icons/fi";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

// Standard business hours
const HOURS = [
  "8:00am", "9:00am", "10:00am", "11:00am", "12:00pm",
  "1:00pm", "2:00pm", "3:00pm", "4:00pm"
];

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error("ErrorBoundary caught:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="p-6 bg-red-50 border border-red-200 rounded-lg">
          <h3 className="text-lg font-medium text-red-800">Something went wrong</h3>
          <p className="mt-2 text-red-600">{this.state.error?.message || "Unknown error"}</p>
          <button
            onClick={() => {
              this.setState({ hasError: false, error: null });
              window.location.reload();
            }}
            className="mt-4 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors"
          >
            Reload Component
          </button>
        </div>
      );
    }
    return this.props.children;
  }
}

export default function MyBookings() {
  const { user, logout, loadingAuth } = useAuth();
  const [bookings, setBookings] = useState([]);
  const [upcomingBookings, setUpcomingBookings] = useState([]);
  const [pastBookings, setPastBookings] = useState([]);
  const [statusMsg, setStatusMsg] = useState("");
  const [statusType, setStatusType] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [newStartTime, setNewStartTime] = useState("");
  const [newEndTime, setNewEndTime] = useState("");
  const [bookedSlots, setBookedSlots] = useState([]);
  const [selectedBooking, setSelectedBooking] = useState(null);
  const [isLoadingSlots, setIsLoadingSlots] = useState(false);
  const [slotError, setSlotError] = useState(null);
  const [isLoadingBookings, setIsLoadingBookings] = useState(true);

  const navigate = useNavigate();
  const API_URL = process.env.REACT_APP_API_URL;

  // Utility functions
  const formatTimeForDisplay = useCallback((timeStr) => {
    if (!timeStr) return "";
    if (timeStr.includes('am') || timeStr.includes('pm')) {
      return timeStr.toLowerCase().replace(/\s/g, '');
    }
    const [hours, minutes] = timeStr.split(':');
    const hourNum = parseInt(hours, 10);
    const period = hourNum >= 12 ? 'pm' : 'am';
    const displayHour = hourNum % 12 === 0 ? 12 : hourNum % 12;
    return `${displayHour}:${minutes}${period}`;
  }, []);

  const timeToMinutes = useCallback((timeStr) => {
    if (!timeStr) return 0;
    const timePart = timeStr.split(/(am|pm)/i)[0];
    const period = timeStr.toLowerCase().includes('pm') ? 'pm' : 'am';
    const [hours, minutes] = timePart.split(':').map(Number);
    let total = hours * 60 + minutes;
    if (period === 'pm' && hours !== 12) total += 12 * 60;
    if (period === 'am' && hours === 12) total -= 12 * 60;
    return total;
  }, []);

  const isTimeSlotBooked = useCallback((checkTime) => {
    const checkMinutes = timeToMinutes(checkTime);
    return bookedSlots.some((booked) => {
      const bookedStart = timeToMinutes(booked.start);
      const bookedEnd = timeToMinutes(booked.end);
      return (
        checkMinutes >= bookedStart && 
        checkMinutes < bookedEnd &&
        !(selectedBooking && 
          booked.start === formatTimeForDisplay(selectedBooking.time) && 
          booked.end === formatTimeForDisplay(selectedBooking.end_time))
      );
    });
  }, [bookedSlots, timeToMinutes, selectedBooking, formatTimeForDisplay]);

  const isTimeRangeAvailable = useCallback((startTime, endTime) => {
    if (!startTime || !endTime) return true;
    const startMinutes = timeToMinutes(startTime);
    const endMinutes = timeToMinutes(endTime);
    if (startMinutes >= endMinutes) return false;
    return !bookedSlots.some((booked) => {
      const bookedStart = timeToMinutes(booked.start);
      const bookedEnd = timeToMinutes(booked.end);
      return (
        (startMinutes >= bookedStart && startMinutes < bookedEnd) ||
        (endMinutes > bookedStart && endMinutes <= bookedEnd) ||
        (startMinutes <= bookedStart && endMinutes >= bookedEnd)
      );
    });
  }, [bookedSlots, timeToMinutes]);

  // Data fetching
  const fetchBookings = useCallback(async () => {
    if (loadingAuth || !user?.email) return;
    
    setIsLoadingBookings(true);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get(
        `${API_URL}/api/booking/user-bookings?email=${user.email}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const now = new Date();
      const [upcoming, past] = response.data.reduce((acc, booking) => {
        const bookingDate = new Date(booking.date);
        if (isNaN(bookingDate.getTime())) {
          acc[1].push(booking);
          return acc;
        }

        if (bookingDate > now) {
          acc[0].push(booking);
        } else {
          acc[1].push(booking);
        }
        return acc;
      }, [[], []]);

      setUpcomingBookings(upcoming);
      setPastBookings(past);
      setBookings(response.data);
    } catch (error) {
      console.error("Failed to load bookings:", error);
      if (error.response?.status === 401) {
        logout();
        navigate("/login");
      }
    } finally {
      setIsLoadingBookings(false);
    }
  }, [user, loadingAuth, API_URL, logout, navigate]);

  const fetchBookedSlots = useCallback(async (date, room) => {
    setIsLoadingSlots(true);
    try {
      const token = localStorage.getItem("authToken");
      const res = await axios.get(
        `${API_URL}/api/booking/slots?date=${date}&room=${room}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setBookedSlots(res.data.map(slot => ({
        start: formatTimeForDisplay(slot.time),
        end: formatTimeForDisplay(slot.end_time)
      })));
    } catch (error) {
      console.error("Failed to load slots:", error);
      setSlotError(error.response?.data?.message || "Error loading slots");
    } finally {
      setIsLoadingSlots(false);
    }
  }, [API_URL, formatTimeForDisplay]);

  // Booking actions
  const handleCancel = useCallback(async (id) => {
    try {
      const token = localStorage.getItem("authToken");
      await axios.post(
        `${API_URL}/api/booking/cancel`,
        { id, email: user.email },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setStatusType("success");
      setStatusMsg("Booking cancelled");
      fetchBookings();
    } catch (error) {
      setStatusType("error");
      setStatusMsg(error.response?.data?.message || "Failed to cancel");
    }
  }, [user, API_URL, fetchBookings]);

  const handleEdit = useCallback((booking) => {
    if (booking.status === 'approved' || booking.status === 'cancelled') {
      setStatusType("error");
      setStatusMsg(`Cannot edit ${booking.status} bookings`);
      return;
    }
    setEditingId(booking.id);
    setNewStartTime(formatTimeForDisplay(booking.time));
    setNewEndTime(formatTimeForDisplay(booking.end_time));
    setSelectedBooking(booking);
    fetchBookedSlots(booking.date, booking.room);
  }, [fetchBookedSlots, formatTimeForDisplay]);

  const saveEdit = useCallback(async () => {
    if (!newStartTime || !newEndTime) {
      setStatusType("error");
      setStatusMsg("Select both times");
      return;
    }

    try {
      const token = localStorage.getItem("authToken");
      await axios.post(
        `${API_URL}/api/booking/edit`,
        {
          id: editingId,
          newTime: newStartTime,
          newEndTime: newEndTime,
          email: user.email
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setStatusType("success");
      setStatusMsg("Booking updated");
      setEditingId(null);
      fetchBookings();
    } catch (error) {
      setStatusType("error");
      setStatusMsg(error.response?.data?.message || "Failed to update");
    }
  }, [editingId, newStartTime, newEndTime, user, API_URL, fetchBookings]);

  // Effects
  useEffect(() => {
    if (!loadingAuth && user) fetchBookings();
  }, [loadingAuth, user, fetchBookings]);

  // Render
  return (
    <ErrorBoundary>
      <div className="flex min-h-screen bg-gray-50">
        {/* Sidebar */}
        <div className="w-64 bg-gradient-to-b from-green-700 to-green-600 shadow-xl hidden md:flex flex-col p-6 text-white">
          {/* ... sidebar content ... */}
        </div>

        {/* Main Content */}
        <div className="flex-1 p-6">
          <header className="bg-white shadow-sm p-6 mb-6 rounded-lg">
            <h1 className="text-2xl font-bold text-gray-800">My Bookings</h1>
          </header>

          {statusMsg && (
            <motion.div
              className={`p-4 mb-6 rounded-lg ${
                statusType === "success" 
                  ? "bg-green-100 text-green-800" 
                  : "bg-red-100 text-red-800"
              }`}
            >
              {statusMsg}
            </motion.div>
          )}

          {isLoadingBookings ? (
            <div className="flex justify-center items-center h-48">
              <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-green-600"></div>
            </div>
          ) : (
            <div className="space-y-8">
              {/* Upcoming Bookings */}
              <div>
                <h2 className="text-xl font-bold text-gray-800 mb-4">
                  Upcoming Bookings ({upcomingBookings.length})
                </h2>
                <div className="grid grid-cols-1 gap-4">
                  {upcomingBookings.map(booking => (
                    <motion.div 
                      key={booking.id}
                      className="bg-white rounded-lg shadow-sm p-6"
                      whileHover={{ y: -2 }}
                    >
                      <div className="flex justify-between">
                        <div>
                          <h3 className="font-medium">{booking.room}</h3>
                          <p className="text-gray-600">
                            {new Date(booking.date).toLocaleDateString()} â€¢ 
                            {formatTimeForDisplay(booking.time)} - 
                            {formatTimeForDisplay(booking.end_time)}
                          </p>
                        </div>
                        <span className={`px-2 py-1 rounded text-xs ${
                          booking.status === 'approved' ? 'bg-green-100 text-green-800' :
                          booking.status === 'pending' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {booking.status}
                        </span>
                      </div>

                      {editingId === booking.id ? (
                        <div className="mt-4 space-y-3">
                          <div className="grid grid-cols-2 gap-3">
                            <div>
                              <label className="block text-sm mb-1">Start</label>
                              <select
                                className="w-full border rounded p-2"
                                value={newStartTime}
                                onChange={(e) => {
                                  setNewStartTime(e.target.value);
                                  setNewEndTime("");
                                }}
                              >
                                <option value="">Select time</option>
                                {HOURS.map(h => (
                                  <option
                                    key={h}
                                    value={h}
                                    disabled={isTimeSlotBooked(h) && h !== formatTimeForDisplay(booking.time)}
                                  >
                                    {h}{isTimeSlotBooked(h) && " (Booked)"}
                                  </option>
                                ))}
                              </select>
                            </div>
                            <div>
                              <label className="block text-sm mb-1">End</label>
                              <select
                                className="w-full border rounded p-2"
                                value={newEndTime}
                                onChange={(e) => setNewEndTime(e.target.value)}
                                disabled={!newStartTime}
                              >
                                <option value="">Select time</option>
                                {HOURS.map(h => {
                                  const isValid = timeToMinutes(h) > timeToMinutes(newStartTime) && 
                                                (booking.status !== 'pending' || 
                                                 isTimeRangeAvailable(newStartTime, h) ||
                                                 h === formatTimeForDisplay(booking.end_time));
                                  return (
                                    <option
                                      key={h}
                                      value={h}
                                      disabled={!isValid}
                                    >
                                      {h}{!isValid && " (Unavailable)"}
                                    </option>
                                  );
                                })}
                              </select>
                            </div>
                          </div>
                          <div className="flex justify-end space-x-2">
                            <button
                              onClick={() => setEditingId(null)}
                              className="px-3 py-1 border rounded"
                            >
                              Cancel
                            </button>
                            <button
                              onClick={saveEdit}
                              className="px-3 py-1 bg-blue-600 text-white rounded"
                            >
                              Save
                            </button>
                          </div>
                        </div>
                      ) : (
                        <div className="mt-4 flex justify-end space-x-2">
                          <button
                            onClick={() => handleEdit(booking)}
                            disabled={booking.status !== 'pending'}
                            className={`px-3 py-1 rounded ${
                              booking.status === 'pending' 
                                ? 'bg-blue-600 text-white' 
                                : 'bg-gray-300 text-gray-600'
                            }`}
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleCancel(booking.id)}
                            disabled={booking.status === 'cancelled'}
                            className={`px-3 py-1 rounded ${
                              booking.status !== 'cancelled'
                                ? 'bg-red-600 text-white'
                                : 'bg-gray-300 text-gray-600'
                            }`}
                          >
                            Cancel
                          </button>
                        </div>
                      )}
                    </motion.div>
                  ))}
                </div>
              </div>

              {/* Past Bookings Section */}
              <div>
                <h2 className="text-xl font-bold text-gray-800 mb-4 flex items-center">
                  <FiChevronRight className="text-blue-600 mr-2" /> Past Bookings
                  ({pastBookings.length})
                </h2>
                <div className="grid grid-cols-1 gap-6">
                  {renderBookingCards(pastBookings)}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </ErrorBoundary>
  );
}
