import React, { useState, useEffect } from "react";
import axios from "axios";
import { useAuth } from "../contexts/AuthContext";
import { Link } from "react-router-dom";
import {
  FiCalendar,
  FiBookOpen,
  FiHome,
  FiLogOut,
  FiCheckCircle,
  FiXCircle,
  FiPlus,
  FiUsers,
  FiMapPin,
  FiWifi,
  FiMonitor,
  FiVideo,
  FiMic,
  FiClock
} from "react-icons/fi";
import { motion } from "framer-motion";
import { useNavigate } from "react-router-dom";

const API_URL = process.env.REACT_APP_API_URL;

export default function BookingCalendar() {
  // ... (keep existing state declarations)

  // Updated hours array for better time selection
  const hours = Array.from({ length: 13 }, (_, i) => {
    const hour = i + 8; // From 8am to 8pm
    const period = hour >= 12 ? 'pm' : 'am';
    const displayHour = hour > 12 ? hour - 12 : hour;
    return `${displayHour}:00${period}`;
  });

  // ... (keep existing helper functions and useEffect hooks)

  // Enhanced room amenities display
  const renderAmenities = (amenities) => {
    if (!amenities) return null;
    
    const amenitiesArray = Array.isArray(amenities)
      ? amenities
      : typeof amenities === 'string'
      ? amenities.split(',')
      : [];
      
    return (
      <div className="mt-3 flex flex-wrap gap-1">
        {amenitiesArray.map((amenity, index) => (
          <span 
            key={index} 
            className="inline-flex items-center text-xs bg-gray-100 rounded-full px-2 py-1 text-gray-600"
          >
            {getAmenityIcon(amenity.trim())}
            {amenity.trim()}
          </span>
        ))}
      </div>
    );
  };

  return (
    <div className="flex min-h-screen bg-gray-50 font-poppins">
      {/* Sidebar */}
      <div className="w-64 bg-gradient-to-b from-green-700 to-green-600 shadow-xl hidden md:flex flex-col p-6 text-white">
        <div className="mb-10">
          <h2 className="text-2xl font-bold mb-1">MeetingHub</h2>
          <p className="text-green-100 text-sm">User Dashboard</p>
        </div>
        
        <nav className="flex-1 space-y-2">
          <Link 
            to="/dashboard-user" 
            className="flex items-center p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all"
          >
            <FiHome className="mr-3 text-lg" />
            <span>Dashboard</span>
          </Link>
          <Link 
            to="/my-bookings" 
            className="flex items-center p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all"
          >
            <FiBookOpen className="mr-3 text-lg" />
            <span>My Bookings</span>
          </Link>
          <Link 
            to="/calendar" 
            className="flex items-center p-3 text-white bg-green-800 rounded-lg transition-all hover:bg-green-900"
          >
            <FiCalendar className="mr-3 text-lg" />
            <span>New Booking</span>
          </Link>
        </nav>
        
        <div className="mt-auto pt-4 border-t border-green-800">
          <div className="px-4 py-3 bg-green-800 rounded-lg">
            <p className="text-sm font-medium">{user?.name}</p>
            <p className="text-xs text-green-200">{user?.email}</p>
          </div>
          <button
            className="flex items-center w-full p-3 text-green-100 hover:bg-green-800 rounded-lg transition-all mt-2"
            onClick={handleLogout}
          >
            <FiLogOut className="mr-3 text-lg" />
            <span>Logout</span>
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-6">
        <header className="bg-white shadow-sm p-6 mb-6 rounded-lg">
          <h1 className="text-2xl font-bold text-gray-800">
            <FiCalendar className="inline-block mr-2" />
            Book a Meeting Room
          </h1>
          <p className="text-gray-600">
            Select a date, time, and room for your meeting
          </p>
        </header>

        {confirmationMsg && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className={`mb-6 p-4 rounded-lg ${
              status === "success"
                ? "bg-green-100 text-green-800 border border-green-200"
                : "bg-red-100 text-red-800 border border-red-200"
            }`}
          >
            <div className="flex items-center">
              {status === "success" ? (
                <FiCheckCircle className="mr-2" />
              ) : (
                <FiXCircle className="mr-2" />
              )}
              <span>{confirmationMsg}</span>
            </div>
          </motion.div>
        )}

        {/* Room Cards Section */}
        <motion.div 
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-xl shadow-sm p-6 mb-6"
        >
          <h2 className="text-xl font-semibold text-gray-800 mb-4">Available Meeting Rooms</h2>
          
          {isLoadingRooms ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {[1, 2, 3].map((i) => (
                <div key={i} className="animate-pulse bg-gray-200 rounded-lg h-40"></div>
              ))}
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {rooms.map((roomItem) => (
                <div 
                  key={roomItem.id} 
                  className={`border rounded-lg p-4 transition-all cursor-pointer hover:shadow-md ${
                    room === roomItem.name 
                      ? "border-green-500 bg-green-50" 
                      : "border-gray-200"
                  }`}
                  onClick={() => setRoom(roomItem.name)}
                >
                  <div className="flex justify-between items-start">
                    <h3 className="font-medium text-lg text-gray-800">{roomItem.name}</h3>
                    <span className="flex items-center text-sm text-gray-600">
                      <FiUsers className="mr-1" /> {roomItem.capacity} people
                    </span>
                  </div>
                  <div className="flex items-center text-sm text-gray-500 mt-1">
                    <FiMapPin className="mr-1" /> {roomItem.location}
                  </div>
                  <p className="text-sm text-gray-600 mt-2 line-clamp-2">{roomItem.description}</p>
                  <div className="mt-3">
                    <div className="flex flex-wrap gap-2">
  {(() => {
    if (!roomItem.amenities) return null;
    
    const amenitiesArray = Array.isArray(roomItem.amenities)
      ? roomItem.amenities
      : typeof roomItem.amenities === 'string'
      ? roomItem.amenities.split(',')
      : [];
      
    return amenitiesArray.map((amenity, index) => (
      <span 
        key={index} 
        className="flex items-center text-xs bg-gray-100 rounded-full px-2 py-1 text-gray-600"
      >
        {getAmenityIcon(amenity.trim())}
        {amenity.trim()}
      </span>
    ));
  })()}
</div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </motion.div>

        {/* Booking Form */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-xl shadow-sm p-6 max-w-2xl mx-auto"
        >
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Select a Date
              </label>
              <input
                type="date"
                min={today}
                value={selectedDate}
                className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-2 border"
                onChange={(e) => {
                  setSelectedDate(e.target.value);
                  setStartTime("");
                  setEndTime("");
                  setConfirmationMsg("");
                  setStatus("");
                }}
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Meeting Room
              </label>
              {isLoadingRooms ? (
                <div className="animate-pulse bg-gray-200 rounded-lg h-10 w-full"></div>
              ) : (
                <select
                  className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-2 border"
                  value={room}
                  onChange={(e) => {
                    setRoom(e.target.value);
                    setStartTime("");
                    setEndTime("");
                    setConfirmationMsg("");
                    setStatus("");
                  }}
                >
                  <option value="">-- Select Room --</option>
                  {rooms.map((roomItem) => (
                    <option key={roomItem.id} value={roomItem.name}>
                      {roomItem.name} (Capacity: {roomItem.capacity})
                    </option>
                  ))}
                </select>
              )}
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Start Time
                </label>
                <select
                  className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-2 border"
                  value={startTime}
                  onChange={(e) => {
                    setStartTime(e.target.value);
                    setEndTime("");
                  }}
                  disabled={!selectedDate || !room || isLoading}
                >
                  <option value="">-- Select --</option>
                  {hours.map((h) => {
                    const isBooked = isTimeSlotBooked(h);
                    return (
                      <option
                        key={h}
                        value={h}
                        disabled={isBooked}
                        className={isBooked ? "text-gray-400" : ""}
                      >
                        {formatTime(h)}{isBooked ? " (Booked)" : ""}
                      </option>
                    );
                  })}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  End Time
                </label>
                <select
                  className="block w-full rounded-lg border-gray-300 shadow-sm focus:border-green-500 focus:ring-green-500 p-2 border"
                  value={endTime}
                  onChange={(e) => setEndTime(e.target.value)}
                  disabled={!startTime || isLoading}
                >
                  <option value="">-- Select --</option>
                  {hours.map((h) => {
                    const isAfterStart = startTime && timeToMinutes(h) > timeToMinutes(startTime);
                    const isAvailable = isAfterStart && isTimeRangeAvailable(startTime, h);
                    
                    return (
                      <option
                        key={h}
                        value={h}
                        disabled={!isAvailable}
                        className={!isAvailable ? "text-gray-400" : ""}
                      >
                        {formatTime(h)}{!isAfterStart ? " (Before start)" : !isAvailable ? " (Unavailable)" : ""}
                      </option>
                    );
                  })}
                </select>
              </div>
            </div>

            <button
              className={`w-full bg-green-600 hover:bg-green-700 text-white py-3 px-4 rounded-lg shadow-sm font-medium transition-colors duration-150 ease-in-out focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 mt-4 ${
                isLoading ? "opacity-70 cursor-not-allowed" : ""
              }`}
              onClick={confirmBooking}
              disabled={isLoading}
            >
              {isLoading ? (
                "Processing..."
              ) : (
                <>
                  <FiPlus className="inline-block mr-2" />
                  Confirm Booking
                </>
              )}
            </button>
          </div>
        </motion.div>
      </div>
    </div>
  );
}
